%% Single Side Band Modulation: Weaver's Method
clc;
close all;
clear;
%% Parameters
%%%%%%%% generating a message signal %%%%%%%%%%
%fs = 44100; % Sampling frequency
%t = linspace(0,10,10*fs);
%freq = [1000,200,500,5000,2000,3500,4000];
%ms_org = zeros(1,length(t));
%for i = 1:7
%    ms_org = ms_org + sin(2*pi*freq(i)*t);
%end
%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
%%%%% Reading from wav file %%%%%%%%%%%%%%%%%%%
[ms_org, fs_org] = audioread("sample_input.wav");
ms_org = transpose(ms_org);
%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
B = 3300; % max frequency in message
fs = 44100;
fo = B/2; 
fc = 20000; % carrier frequency
f1 = fc - (B/2);
ms_filtered = lowpass(ms_org, B, fs_org, 'ImpulseResponse', 'iir'); %passing message through low pass filter
%ms_filtered = ms_org;

%% Resample
ms = resample(ms_filtered, fs, fs_org);

%% Modulation
t = linspace(0,length(ms)/fs,length(ms));
% Step 1: Multiplier 1
m1 = ms .* cos(2*pi*fo*t);
m2 = ms .* sin(2*pi*fo*t);
% Step 2: low pass filter
m3 = lowpass(m1, B/2, fs, 'ImpulseResponse', 'iir');
m4 = lowpass(m2, B/2, fs, 'ImpulseResponse', 'iir');
% Step 3: Multiplier 2
m5 = m3 .* cos(2*pi*f1*t);
m6 = m4 .* sin(2*pi*f1*t);
% Step 4: Addition
mod_out = m5 - m6;
% Step 5: Amplify
mod_out = mod_out .* 2 * 250;

