%% AWGN 
%recv = awgn(recv,2,'measured'); % white gaussian noice added to signal

%% Demodulation
t_recv = linspace(0,length(recv)/fs,length(recv));
% Step 1: Multiplier 1
m7 = recv .* cos(2*pi*f1*t_recv);
m8 = recv .* sin(2*pi*f1*t_recv);
% Step 2: low pass filter
m9 = lowpass(m7, B/2, fs, 'ImpulseResponse', 'iir');
m10 = lowpass(m8, B/2, fs, 'ImpulseResponse', 'iir');
% Step 3: Multiplier 2
m11 = m9 .* cos(2*pi*fo*t_recv);
m12 = m10 .* sin(2*pi*fo*t_recv);
% Step 4: Addition
demod_out = m11 + m12;
% Step 5: Amplify
demod_out = demod_out .* 2;

%% SNR calculation
