% snr_out = snr(demod_out,noise);

%% Plotting
n = length(ms); 
f =  (-n/2:n/2-1)*(fs/n);
nr = length(demod_out); 
fr =  (-nr/2:nr/2-1)*(fs/nr);
% message
figure(1);
subplot(2,1,1); 
plot(t,ms); 
title('Message');
ylabel('Amplitude '); xlabel('Time '); 

