plot(f,fftshift(abs(fft(ms))),'LineWidth',2);
title('Spectrum of message signal'); 
xlabel('frequency'); ylabel('magnitude');

% modulated message
figure(2);
subplot(2,1,1); 
plot(t,mod_out); 
title('Modulated Message');
ylabel('Amplitude '); xlabel('Time ');

subplot(2,1,2);
plot(f,fftshift(abs(fft(mod_out))),'LineWidth',2);
title('Spectrum of modulated signal'); 
xlabel('frequency'); ylabel('magnitude');

% demodulated message
figure(3);
subplot(2,1,1); 
plot(t_recv,demod_out); 
title('Demodulated Message');
ylabel('Amplitude '); xlabel('Time ');

subplot(2,1,2);
plot(fr,fftshift(abs(fft(demod_out))),'LineWidth',2);
title('Spectrum of demodulated signal'); 
